mapper.select:
select 

from pms_ttrade_sum
where port_id = #{portId,jdbcType=INTEGER}
  and business_date = #{businessDate,jdbcType=INTEGER}
  and entrust_direction = #{entrustDirection,jdbcType=INTEGER}
  and inter_code = #{interCode,jdbcType=INTEGER}

mapper.delete:
delete from pms_ttrade_sum
where port_id = #{portId,jdbcType=INTEGER}
  and business_date = #{businessDate,jdbcType=INTEGER}
  and entrust_direction = #{entrustDirection,jdbcType=INTEGER}
  and inter_code = #{interCode,jdbcType=INTEGER}

mapper.update:
update pms_ttrade_sum
set port_name = #{portName,jdbcType=VARCHAR},
  entrust_direction_name = #{entrustDirectionName,jdbcType=VARCHAR},
  deal_amount = #{dealAmount,jdbcType=DECIMAL},
  deal_balance = #{dealBalance,jdbcType=DECIMAL},
  deal_avg_price = #{dealAvgPrice,jdbcType=DECIMAL},
  closing_price = #{closingPrice,jdbcType=DECIMAL},
  today_profit = #{todayProfit,jdbcType=DECIMAL},
  market_deal_percent = #{marketDealPercent,jdbcType=DECIMAL},
  asset_proportion = #{assetProportion,jdbcType=DECIMAL},
  stock_code = #{stockCode,jdbcType=VARCHAR},
  stock_name = #{stockName,jdbcType=VARCHAR}
where port_id = #{portId,jdbcType=INTEGER}
  and business_date = #{businessDate,jdbcType=INTEGER}
  and entrust_direction = #{entrustDirection,jdbcType=INTEGER}
  and inter_code = #{interCode,jdbcType=INTEGER}

